using System;
using System.Net;
using System.Net.Http;
using System.Text;

namespace ReceiptVerify
{
    class Program
    {
        static void Main(string[] args)
        {
            Console.WriteLine("Hello World!");
            Apple();

            Console.ReadLine();
        }

        static void Apple()
        {
            using(var client = new HttpClient())
            {
                ServicePointManager.SecurityProtocol = SecurityProtocolType.Tls12;
                client.Timeout = TimeSpan.FromSeconds(11);

                string data = "{\"receipt-data\" : \"" + "" + "\"}";
                var content = new StringContent(data, Encoding.UTF8, "application/json");
                var httpResult = client.PostAsync("https://sandbox.itunes.apple.com/verifyReceipt", content).Result;
                var response = httpResult.Content.ReadAsStringAsync().Result;
            }
        }
    }
}
